import React, { useState } from "react";
import styled from "styled-components";
import styles from "../styles/Bridge.module.css";

const Container = styled.div`
  background: linear-gradient(#91b5c9 40%, #b6cfdc);
  background-repeat: no-repeat;
  background-size: cover;
  height: 110%;
  padding: 55px 120px;
`;

const Title = styled.h1`
  width: fit-content;
  height: auto;
  margin-left: 14px;
  border-bottom: 3px solid #000;
  font-size: 65px;
  color: #000;
  font-family: "Signika", sans-serif;
`;

const Daegyo = styled.p`
  width: 407px;
  height: 545px;
  margin: 50px 0 0 0;
  margin: 0 9px;
  color: #000;
  border: 1px solid red;
  border-radius: 5px;
  font-size: 23px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #fff;
  justify-self: center;
`;

const DaegyoWrapper = styled.div`
  width: auto;
  height: 545px;
  display: flex;
  justify-content: space-between;
`;

const DaegyoList = styled.div`
  width: 100%;
  height: 545px;
  margin-top: 50px;
  display: flex;
  overflow: hidden;
`;
function Bridge() {
  const [lists, setLists] = useState([
    {
      id: 1,
      text1: "일산대교",
      text2: "김포대교",
      text3: "행주대교",
      img1: "data:image/jpeg;base64,",
      img2: "data:image/jpeg;base64,",
      img3: "data:image/jpeg;base64,",
    },
    {
      id: 2,
      text1: "방화대교",
      text2: "마곡철교",
      text3: "가양대교",
    },
    {
      id: 3,
      text1: "월드컵대교",
      text2: "성산대교",
      text3: "양화대교",
    },
    {
      id: 4,
      text1: "당산철교",
      text2: "서강대교",
      text3: "마포대교",
    },
    {
      id: 5,
      text1: "원효대교",
      text2: "한강철교",
      text3: "한강대교",
    },
    {
      id: 6,
      text1: "노량대교",
      text2: "동작대교",
      text3: "반포대교",
    },
    {
      id: 7,
      text1: "한남대교",
      text2: "동호대교",
      text3: "성수대교",
    },
    {
      id: 8,
      text1: "영동대교",
      text2: "청담대교",
      text3: "잠실대교",
    },
    {
      id: 9,
      text1: "잠실철교",
      text2: "올림픽대교",
      text3: "천호대교",
    },
    {
      id: 10,
      text1: "광진교",
      text2: "구리암사대교",
      text3: "강동대교",
    },
    {
      id: 11,
      text1: "고덕대교",
      text2: "미사대교",
      text3: "팔당대교",
    },
  ]);

  const daegyoList = lists.map((list) => (
    <DaegyoWrapper>
      <Daegyo
        className={styles.img}
        style={{ backgroundImage: `url(${list.img1})` }}
      >
        {list.text1}
      </Daegyo>
      <Daegyo
        className={styles.img}
        style={{ backgroundImage: `url(${list.img2})` }}
      >
        {list.text2}
      </Daegyo>
      <Daegyo
        className={styles.img}
        style={{ backgroundImage: `url(${list.img3})` }}
      >
        {list.text3}
      </Daegyo>
    </DaegyoWrapper>
  ));

  return (
    <Container>
      <Title className={styles.h1}>THE BRIDGES</Title>
      <DaegyoList>{daegyoList}</DaegyoList>
    </Container>
  );
}

export default Bridge;
